def load_image_from_b64(b64str, size):
    if not PIL_OK:
        return None
    # Satır sonu karakterlerini temizle (Gemini'nin uyardığı sorun)
    clean = b64str.replace("\n", "").replace("\r", "").replace(" ", "")
    data = base64.b64decode(clean.encode())
    img = Image.open(io.BytesIO(data)).convert("RGBA")
    img = img.resize(size, Image.LANCZOS)
    return img


def make_glow_image(img, color=(200, 160, 70), radius=18, strength=2.0):
    if not PIL_OK:
        return img
    glow = img.filter(ImageFilter.GaussianBlur(radius))
    r, g, b = color
    for _ in range(int(strength)):
        glow = Image.alpha_composite(
            Image.new("RGBA", img.size, (0, 0, 0, 0)), glow
        )
    result = Image.new("RGBA", img.size, (0, 0, 0, 0))
    result = Image.alpha_composite(result, glow)
    result = Image.alpha_composite(result, img)
    return result


class ParticleCanvas(tk.Canvas):
    """Floating dust/ember particle system"""
    def __init__(self, master, w, h, **kw):
        super().__init__(master, width=w, height=h, bg=C["bg"],
                         highlightthickness=0, **kw)
        self.w, self.h = w, h
        self.particles = []
        self._init_particles()
        self._animate()

    def _init_particles(self):
        for _ in range(55):
            self.particles.append({
                "x": random.uniform(0, self.w),
                "y": random.uniform(0, self.h),
                "vx": random.uniform(-0.3, 0.3),
                "vy": random.uniform(-0.5, -0.1),
                "r": random.uniform(1, 3),
                "alpha": random.uniform(0.2, 0.9),
                "color": random.choice(["#c9a84c","#8b0000","#4a3060","#c9a84c","#ffffff"]),
                "id": None,
            })

    def _animate(self):
        self.delete("particle")
        for p in self.particles:
            p["x"] += p["vx"]
            p["y"] += p["vy"]
            if p["y"] < -5:
                p["y"] = self.h + 5
                p["x"] = random.uniform(0, self.w)
            if p["x"] < -5: p["x"] = self.w + 5
            if p["x"] > self.w + 5: p["x"] = -5
            r = p["r"]
            self.create_oval(
                p["x"]-r, p["y"]-r, p["x"]+r, p["y"]+r,
                fill=p["color"], outline="", tags="particle"
            )
        self.after(40, self._animate)


class HPBar(tk.Canvas):
    def __init__(self, master, w=160, h=16, fill_color="#ff2222", bg_color="#1a0000", label="HP", **kw):
        super().__init__(master, width=w, height=h, bg=bg_color,
                         highlightthickness=1, highlightbackground="#4a1111", **kw)
        self.w, self.h = w, h
        self.fill_color = fill_color
        self.label = label
        self._pct = 1.0
        self._draw()

    def set(self, pct):
        self._pct = max(0.0, min(1.0, pct))
        self._draw()

    def _draw(self):
        self.delete("all")
        fw = int(self.w * self._pct)
        if fw > 0:
            self.create_rectangle(0, 0, fw, self.h, fill=self.fill_color, outline="")
        # shimmer
        for i in range(0, fw, 6):
            self.create_line(i, 0, i, self.h//3, fill="#ffffff", stipple="gray25")
        self.create_text(self.w//2, self.h//2, text=self.label,
                         fill="white", font=("Courier", 8, "bold"))


