    # ──────────────────────────────────────────────
    def _log(self, msg, color=None):
        self.log_text.config(state="normal")
        tag = f"c{len(self.log_text.get('1.0','end'))}"
        self.log_text.insert("end", msg + "\n", tag)
        if color:
            self.log_text.tag_config(tag, foreground=color)
        self.log_text.see("end")
        self.log_text.config(state="disabled")

    def _clear_log(self):
        self.log_text.config(state="normal")
        self.log_text.delete("1.0", "end")
        self.log_text.config(state="disabled")

    # ──────────────────────────────────────────────
    def _show_intro(self):
        self._log("═══════════════════════════════════", C["gold"])
        self._log("   ⚡ PHOTONS – ŞİFU'YA KARŞI ⚡   ", C["gold"])
        self._log("═══════════════════════════════════", C["gold"])
        self._log("Bilge Şifu'nun karşısına geçtin.", C["text"])
        self._log("Aklındaki sayıyı bulmaya çalış!", C["text"])
        self._log("Şifu ise seninle yarışıyor...", C["dim"])
        self._log("▶ BAŞLAT ile ilk aşamaya gir.", C["green2"])

    # ──────────────────────────────────────────────
    def tick(self):
        if not self.timer_running:
            return
        if self.time_left <= 0:
            self._log("⏰ Süre doldu! Şifu kazandı.", C["red2"])
            self.end_stage(False)
            return
        m, s = divmod(self.time_left, 60)
        self.time_lbl.config(text=f"⏳ {m:02d}:{s:02d} | ❤ {self.attempts_left}")
        self.time_left -= 1
        self.after(1000, self.tick)

    # ──────────────────────────────────────────────
    def start_stage(self):
        if self.stage > 5:
            self._log("Tüm aşamaları tamamladın! Efsane!", C["gold"])
            return

        cfg = DIFFICULTIES[self.stage]
        lo, hi = cfg["range"]

        self.target = random.randint(lo, hi)
        self.attempts_left = cfg["attempts"]
        self.time_left = cfg["time"]
        self.timer_running = True
        self.stage_active = True

        self.ai_low, self.ai_high = lo, hi
        self.shifu_guess_count = 0
        self.bad_moves = 0
        self.player_history.clear()

        self.shifu_rage_mode = self.shifu_anger > 20

        # Reset HP
        self.player_hp = 100
        self.shifu_hp = 100
        self.player_hp_bar.set(1.0)
        self.shifu_hp_bar.set(1.0)
        self.xp_bar.set(0)
        self.anger_bar.set(min(1.0, self.shifu_anger / 25))

        title, intro = STAGE_INTROS.get(self.stage, ("Savaş", "Başlıyor!"))
        sname = cfg["name"]

        self.stage_badge.config(
            text=f"AŞAMA {self.stage} – {sname}")

        self._clear_log()
        self._log(f"━━━ AŞAMA {self.stage}: {title} ━━━", C["gold"])
        self._log(intro, C["text"])
        self._log(f"📖 Aralık: {lo}–{hi}  |  Hak: {self.attempts_left}", C["dim"])

        if self.shifu_rage_mode:
            self._log("💢 Şifu ÖFKE modunda! 3. hamlede bilebilir!", C["red2"])
            self.shifu_emotion.config(text="😡 ÖFKELI", fg=C["red2"])
        else:
            self.shifu_emotion.config(text="😌 Sakin", fg=C["gold"])

        self.status_lbl.config(text="Sayını gir ve ⚔ TAHMİN'e bas!")
        self.entry.focus()
        self.tick()

    # ──────────────────────────────────────────────
    def make_guess(self):
        if not self.timer_running or not self.stage_active:
            return
        try:
            g = int(self.entry.get())
        except ValueError:
            self._log("⚠ Geçerli bir sayı gir!", C["red2"])
            return

        self.entry.delete(0, tk.END)
        lo, hi = DIFFICULTIES[self.stage]["range"]

        # analiz
        self.player_history.append(g)
        if len(self.player_history) == 1:
            span = hi - lo
            if g < lo + span * 0.33:
                self.player_low_first += 1
            elif g > lo + span * 0.66:
                self.player_high_first += 1
            else:
                self.player_mid_first += 1

        if g < lo or g > hi:
            self.bad_moves += 1
            self._log(f"⚠ {g} aralık dışında! (Troll +1)", C["red2"])

        self.attempts_left -= 1
        used = DIFFICULTIES[self.stage]["attempts"] - self.attempts_left
        xp_pct = used / DIFFICULTIES[self.stage]["attempts"]
        self.xp_bar.set(xp_pct)

        if g == self.target:
            self.score += DIFFICULTIES[self.stage]["score"]
            self.score_lbl.config(text=f"✦ Puan: {self.score}")
            line = random.choice(PLAYER_WIN_LINES)
            self._log(f"🏆 {line}", C["green2"])
            self._log(f"+{DIFFICULTIES[self.stage]['score']} puan kazandın!", C["gold2"])
            self.shifu_hp = max(0, self.shifu_hp - 40)
            self.shifu_hp_bar.set(self.shifu_hp / 100)
            # 🎉 Kazanma animasyonları
            self._victory_glow(self.player_canvas)
            self._animate_damage(self.shifu_hp_bar, self.shifu_emotion, is_player=False)
            self._animate_score(DIFFICULTIES[self.stage]["score"], 430, 340)
            self._glow_label(self.score_lbl, C["green2"], C["gold2"], flashes=2, interval=350)
            self.shifu_lose_react()
            self.end_stage(True)
            return

        if g < self.target:
            self._log(f"📈 {g} → Daha BÜYÜK bir sayı!", C["blue2"])
        else:
            self._log(f"📉 {g} → Daha KÜÇÜK bir sayı!", C["red2"])

        # Player HP düşer
        dmg = max(5, int(40 / DIFFICULTIES[self.stage]["attempts"]))
        self.player_hp = max(0, self.player_hp - dmg)
        self.player_hp_bar.set(self.player_hp / 100)
        # 💥 Hasar animasyonu
        self._animate_damage(self.player_hp_bar, self.time_lbl, is_player=True)
        self._flash_border(self.player_canvas, "#ff4400", C["blue2"], flashes=1, interval=280)

        self.shifu_turn()

        if self.attempts_left <= 0:
            self._log("💀 Hakkın bitti!", C["red2"])
            self.end_stage(False)

    # ──────────────────────────────────────────────
    def shifu_turn(self):
        self.shifu_guess_count += 1

        if self.shifu_rage_mode and self.shifu_guess_count >= 3:
            line = random.choice(SHIFU_ANGRY_LINES)
            self._log(f"🔥 {line}", C["red2"])
            self._log(f"💀 Şifu sezdi ve kazandı! -{AI_PENALTY} puan", C["red2"])
            self.score -= AI_PENALTY
            self.score_lbl.config(text=f"✦ Puan: {self.score}")
            self.shifu_anger = 0
            self.anger_bar.set(0)
            self.shifu_emotion.config(text="😤 Tatmin", fg=C["gold"])
            # 🔥 Rage kazanma animasyonu
            self._flash_border(self.shifu_canvas, "#ff2200", C["gold"], flashes=2, interval=350)
            self._defeat_shake(self.player_canvas)
            self.end_stage(False)
            return

        style_counts = {
            "low":  self.player_low_first,
            "mid":  self.player_mid_first,
            "high": self.player_high_first,
        }
        dominant = max(style_counts, key=style_counts.get)
        self.ai_skill = min(1.0, 0.25 + sum(style_counts.values()) * 0.05)

        if random.random() < self.ai_skill:
            if dominant == "low":
                guess = int(self.ai_high * 0.75)
            elif dominant == "high":
                guess = int(self.ai_low * 1.25)
            else:
                guess = (self.ai_low + self.ai_high) // 2
        else:
            guess = random.randint(self.ai_low, self.ai_high)

        guess = max(self.ai_low, min(self.ai_high, guess))

        self._log(f"🧙 Şifu düşünüyor... [{self.ai_low}–{self.ai_high}]", C["purple2"])

        if guess == self.target:
            line = random.choice(SHIFU_WIN_LINES)
            self._log(f"🔮 {line}", C["gold"])
            self._log(f"💔 Şifu doğru buldu! -{AI_PENALTY} puan", C["red2"])
            self.score -= AI_PENALTY
            self.score_lbl.config(text=f"✦ Puan: {self.score}")
            self.player_hp = max(0, self.player_hp - 30)
            self.player_hp_bar.set(self.player_hp / 100)
            # 🔮 Şifu kazanma animasyonları
            self._victory_glow(self.shifu_canvas)
            self._defeat_shake(self.player_canvas)
            self._animate_damage(self.player_hp_bar, self.time_lbl, is_player=True)
            self._glow_label(self.shifu_emotion, C["gold2"], C["gold"], flashes=2, interval=350)
            self.end_stage(False)
            return

        if guess < self.target:
            self.ai_low = guess + 1
        else:
            self.ai_high = guess - 1

        # anger tick
        self.shifu_anger = min(25, self.shifu_anger + 0.5)
        self.anger_bar.set(self.shifu_anger / 25)
        if self.shifu_anger > 15:
            self.shifu_emotion.config(text="😠 Gergin", fg=C["red2"])
        elif self.shifu_anger > 8:
            self.shifu_emotion.config(text="😤 Kızgın", fg="#ffaa00")

    # ──────────────────────────────────────────────
    def shifu_lose_react(self):
        self.shifu_anger += random.randint(1, 5)
        self.anger_bar.set(min(1.0, self.shifu_anger / 25))
        if self.shifu_anger > 20:
            self._log("😡 Şifu: TAKTİĞİNİ ÖĞRENDİM! Öfkem kabarıyor!", C["red2"])
            self.shifu_emotion.config(text="🔥 ÖFKELI!", fg=C["red2"])
        else:
            self._log("😒 Şifu: Seni izliyorum... Bu sefer şanslıydın.", C["dim"])
            self.shifu_emotion.config(text="😒 Gözlüyor", fg="#ffaa00")

    # ──────────────────────────────────────────────
    # ANİMASYONLAR (Epilepsi güvenli: yavaş, yumuşak, 3Hz altı)
    # ──────────────────────────────────────────────

    def _shake_widget(self, widget, times=5, distance=5, delay=60):
        """Karakter/widget hafif titreme — yavaş, yumuşak, güvenli."""
        original_x = widget.winfo_x()
        original_y = widget.winfo_y()

        def step(i, direction):
            if i <= 0:
                widget.place(x=original_x, y=original_y)
                return
            offset = direction * distance * (i / times)
            widget.place(x=original_x + offset, y=original_y)
            self.after(delay, step, i - 1, -direction)

        step(times, 1)

    def _flash_border(self, canvas_widget, color, original_color, flashes=2, interval=300):
        """Karakter çerçevesi yumuşak renk yanması — MAX 2 flash, 300ms aralık (3Hz altı)."""
        count = [0]

        def toggle():
            if count[0] >= flashes * 2:
                canvas_widget.config(highlightbackground=original_color)
                return
            if count[0] % 2 == 0:
                canvas_widget.config(highlightbackground=color)
            else:
                canvas_widget.config(highlightbackground=original_color)
            count[0] += 1
            self.after(interval, toggle)

        toggle()

    def _glow_label(self, label, color_on, color_off, flashes=2, interval=400):
        """Label renk geçişi — çok yavaş, göz yormaz."""
        count = [0]

        def toggle():
            if count[0] >= flashes * 2:
                label.config(fg=color_off)
                return
            label.config(fg=color_on if count[0] % 2 == 0 else color_off)
            count[0] += 1
            self.after(interval, toggle)

        toggle()

    def _animate_score(self, amount, x, y):
        """Puan kazanıldığında yukarı kayan küçük metin."""
        lbl = tk.Label(self.bg_canvas,
                       text=f"+{amount} ✦",
                       fg=C["gold2"], bg=C["bg"],
                       font=("Courier", 13, "bold"))
        lbl.place(x=x, y=y)

        cur_y = [y]
        alpha_steps = 12  # toplam adım

        def rise(step):
            if step <= 0:
                lbl.destroy()
                return
            cur_y[0] -= 3
            # renk koyulaştır (fade efekti için dim renk)
            if step < alpha_steps // 2:
                lbl.config(fg=C["dim"])
            lbl.place(x=x, y=cur_y[0])
            self.after(50, rise, step - 1)

        rise(alpha_steps)

    def _animate_damage(self, hp_bar, label_widget, is_player=True):
        """HP bar hasar anımasyonu — bar hafif titrer, renk geçer."""
        orig_color = C["hp_fill"] if is_player else "#b00000"
        hit_color = "#ff8800"

        hp_bar.fill_color = hit_color
        hp_bar._draw()
        self.after(200, lambda: (setattr(hp_bar, "fill_color", orig_color), hp_bar._draw()))

        # Karakter etiketi kısa sarsılma
        if label_widget:
            self._glow_label(label_widget, "#ff6644", C["gold"], flashes=1, interval=250)

    def _victory_glow(self, canvas_widget):
        """Kazanma anında karakter çerçevesi altın parlıyor — 2 kez, yavaş."""
        self._flash_border(canvas_widget, "#f0d080", C["blue2"], flashes=2, interval=400)

    def _defeat_shake(self, frame_widget):
        """Kaybetme anında karakter hafif sarsılıyor."""
        self._shake_widget(frame_widget, times=4, distance=4, delay=70)

    def _transition_fade(self, callback):
        """Aşama geçişi — log alanı yumuşak karartıp açılır."""
        steps = 8
        colors_dark = ["#0a0a12", "#080810", "#06060e", "#04040c",
                       "#04040c", "#06060e", "#080810", "#0a0a12"]

        def step(i):
            if i >= steps:
                self.log_text.config(bg="#0a0a12")
                if callback:
                    callback()
                return
            self.log_text.config(bg=colors_dark[i])
            self.after(60, step, i + 1)

        step(0)

    # ──────────────────────────────────────────────
    def end_stage(self, won):
        self.timer_running = False
        self.stage_active = False

        if self.bad_moves >= 2 and self.stage > 1:
            self.stage -= 1
            line = random.choice(TROLL_LINES)
            self._log(f"⚠ {line}", C["red2"])

        if won:
            self._log(f"✅ Aşama {self.stage} tamamlandı!", C["green2"])
            self._glow_label(self.stage_badge, C["green2"], C["purple2"], flashes=2, interval=450)
        else:
            self._log(f"❌ Aşama {self.stage} başarısız.", C["red2"])
            self._log(f"🎯 Doğru sayı: {self.target}", C["gold"])
            self._glow_label(self.stage_badge, C["red2"], C["purple2"], flashes=2, interval=450)

        self.stage_break()

    # ──────────────────────────────────────────────
    def stage_break(self):
        def countdown(t):
            if t <= 0:
                self.break_lbl.config(text="")
                self.status_lbl.config(text="▶ BAŞLAT ile devam et!")
                return
            self.break_lbl.config(text=f"⏸ Mola... {t}")
            self.after(1000, countdown, t-1)
        countdown(BREAK_TIME)

    # ──────────────────────────────────────────────
    def next_stage(self):
        if self.stage < 5:
            self.stage += 1
        self._transition_fade(self.start_stage)


